import React from "react";
import styled from "styled-components";
import { title } from "../../../Styles/title";
import { romalOranje } from "../../../Styles/colors";

const InputStyled = styled.input`
  font-size: 18px;
  width: 40px;
  text-align: center;
  border: 1px solid grey;
  outline: none;
`;

const IncrementContainer = styled(title)`
  display: flex;
  height: 24px;
`;

const IncrementButton = styled.div`
  width: 23px;
  color: ${romalOranje};
  font-size: 20px;
  text-align: center;
  cursor: pointer;
  line-height: 23px;
  margin: 0px 10px;
  border: 1px solid ${romalOranje};
  ${({ disabled }) =>
    disabled &&
    `opacity: 0.5; 
     pointer-events: none; 
     `}
  &:hover {
    background-color: #ffe3e3;
  }
`;

export function CableLengthInput({ Cablelength }) {
  return (
    <IncrementContainer>
      <div>Vul de gewenste lengte van de kabel in (meters)</div>
      <IncrementButton
        onClick={() => {
          Cablelength.setValue(Cablelength.value - 1);
        }}
        disabled={Cablelength.value === 1}
      >
        {" "}
        -{" "}
      </IncrementButton>
      <InputStyled {...Cablelength} />
      <IncrementButton
        onClick={() => {
          Cablelength.setValue(Cablelength.value + 1);
        }}
      >
        {" "}
        +{" "}
      </IncrementButton>
    </IncrementContainer>
  );
}
