import React from "react";
import {
  Dialog,
  DialogContent,
  DialogFooter,
  DialogShadow,
  DialogBanner,
  DialogBannerName,
  ConfirmButton,
} from "../../../Styles/DialogStyle";
// import { formatPrice } from "../Data/FoodData";
// import { LengthInput } from "./LengthInput";
// import { useLength } from "../Hooks/useLength";

// export function getPrice(order) {
//   return order.length * order.inkoopprijs;
// }

function CableDialogContainer({
  openCable,
  setOpenCable,
  // setOrders,
  // orders,
}) {
  // const length = useLength(openCable && openCable.length);

  // const isEditing = openCable.index > -1;

  function close() {
    setOpenCable();
  }

  const order = {
    ...openCable,
    // quantity: null,
    // length: length.value,
    // toppings: null,
    // choice: null,
  };

  // function editOrder() {
  //   const newOrders = [...orders];
  //   newOrders[openCable.index] = order;
  //   setOrders(newOrders);
  //   close();
  // }

  function addToOrder() {
    // setOrders([...orders, order]);
    close();
  }

  return (
    <>
      <DialogShadow onClick={close} />
      <Dialog>
        <DialogBanner img={openCable.img}>
          <DialogBannerName> {openCable.typenummer} </DialogBannerName>
          <DialogBannerName> {openCable.kabelgroep} </DialogBannerName>
        </DialogBanner>
        <DialogContent>
          hier kom lengte
          {/* <LengthInput length={length} /> */}
        </DialogContent>
        <DialogFooter>
          {/* <ConfirmButton onClick={isEditing ? editOrder : addToOrder}>
            {isEditing ? "wijzig kabel" : "selecteer de kabel"}{" "}
            {formatPrice(getPrice(order))}
          </ConfirmButton> */}
          <ConfirmButton onClick={addToOrder}>selecteer de kabel</ConfirmButton>
        </DialogFooter>
      </Dialog>
    </>
  );
}

export function CableDialog(props) {
  if (!props.openCable) return null;
  return <CableDialogContainer {...props} />;
}
